mapper.update:
UPDATE Users
SET password=#{password}, isAdmin=#{isAdmin}, loginState=#{loginState}, name=#{name}, address=#{address}, phone=#{phone}, postcode=#{postcode}
WHERE userID=#{userID}

mapper.select:
SELECT userID, username, password, isAdmin, loginState, name, address, phone, postcode
FROM Users
WHERE userID = #{userId}

SELECT userID, username, password, isAdmin, loginState, name, address, phone, postcode
FROM Users
WHERE username = #{username}
